function AuthModal({ type, onClose, onSwitch, onLogin }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");

  const handleSubmit = () => {
    if (type === "login") {
      // Admin credentials
      if (
        email === "admin@southshorepress.com" &&
        password === "admin123"
      ) {
        onLogin({ name: "Admin", email, isAdmin: true });
      } else if (email && password) {
        onLogin({ name: email.split("@")[0], email, isAdmin: false });
      }
    } else {
      if (name && email && password) {
        onLogin({ name, email, isAdmin: false });
      }
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-box" onClick={(e) => e.stopPropagation()}>
        <h2>{type === "login" ? "Welcome Back" : "Create Account"}</h2>
        <p className="sub">
          {type === "login"
            ? "Sign in to your South Shore Press account"
            : "Join the South Shore Press community"}
        </p>
        {type === "signup" && (
          <div className="form-group">
            <label>Full Name</label>
            <input
              className="form-input"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="John Doe"
            />
          </div>
        )}
        <div className="form-group">
          <label>Email</label>
          <input
            className="form-input"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="you@email.com"
          />
        </div>
        <div className="form-group">
          <label>Password</label>
          <input
            className="form-input"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder=""
          />
        </div>
        <button className="form-btn" onClick={handleSubmit}>
          {type === "login" ? "Sign In" : "Create Account"}
        </button>
        <div className="modal-switch">
          {type === "login" ? (
            <>
              Don't have an account?{" "}
              <span onClick={() => onSwitch("signup")}>Sign Up</span>
            </>
          ) : (
            <>
              Already have an account?{" "}
              <span onClick={() => onSwitch("login")}>Log In</span>
            </>
          )}
        </div>
        {type === "login" && (
          <p
            style={{
              textAlign: "center",
              fontSize: 11,
              color: "var(--text-muted)",
              marginTop: 12,
            }}
          >
            Admin: admin@southshorepress.com / admin123
          </p>
        )}
      </div>
    </div>
  );
}

//  App 
export default function App() {
  const [page, setPage] = useState({ type: "home" });
  const [modal, setModal] = useState(null); // 'login' | 'signup' | null
  const [user, setUser] = useState(null);

  const navigate = useCallback(
    (p) => {
      setPage(p);
      window.scrollTo(0, 0);
    },
    []
  );

  return (
    <>
      <style>{CSS}</style>
      <Header
        currentPage={page}
        setPage={navigate}
        user={user}
        setModal={(m) => {
          if (m === "logout") {
            setUser(null);
            navigate({ type: "home" });
          } else {
            setModal(m);
          }
        }}
      />
      <main>
        {page.type === "home" && <HomePage setPage={navigate} />}
        {page.type === "category" && (
          <CategoryPage categoryId={page.id} setPage={navigate} />
        )}
        {page.type === "story" && (
          <StoryPage storyId={page.id} setPage={navigate} />
        )}
        {page.type === "admin" && user?.isAdmin && (
          <AdminPage setPage={navigate} />
        )}
      </main>
      <Footer setPage={navigate} />
      {modal && (modal === "login" || modal === "signup") && (
        <AuthModal
          type={modal}
          onClose={() => setModal(null)}
          onSwitch={(t) => setModal(t)}
          onLogin={(u) => {
            setUser(u);
            setModal(null);
          }}
        />
      )}
    </>
  );
}
